<template>
  <el-transfer v-model="text"
               :data="dic"
               @change="handleChange"></el-transfer>
</template>
<script>
export default {
  name: 'AvueTransfer',
  props: {
    value: {
      type: Array,
    },
    dic: {
      type: Array,
      default: () => []
    }
  },
  data () {
    return {
      text: undefined
    }
  },
  watch: {
    value () {
      this.text = this.value;
    }
  },
  created () {
    this.text = this.value;
  },
  methods: {
    handleChange () {
      this.$emit('input', this.text);
      this.$emit('change', this.text);
    }
  }
}
</script>